package mp.managers;

import mp.plugin1.MiPlugin;
import mp.utils.MessageUtils;
import org.bukkit.Bukkit;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;

import java.util.ArrayList;
import java.util.List;

public class MenuInventoryManager {

 private final MiPlugin plugin;

 public MenuInventoryManager(MiPlugin plugin) {
 this.plugin = plugin;
 }

 public void openAllyEggMenu(Player player) {
 // Crear inventario con 27 slots (3 filas) y un título estilizado
 Inventory inv = Bukkit.createInventory(null, 27, MessageUtils.getColoredMessage("&aElige tu aliado especial"));

 // Llenar borde con vidrios (opcional, si tenés la función)
 fillBorder(inv, Material.STAINED_GLASS_PANE, (short) 5);

 // Gólem de Hierro
 ItemStack golem = new ItemStack(Material.IRON_BLOCK);
 ItemMeta golemMeta = golem.getItemMeta();
 golemMeta.setDisplayName(MessageUtils.getColoredMessage("&f✘ Gólem de Hierro"));
 List<String> golemLore = new ArrayList<>();
 golemLore.add(MessageUtils.getColoredMessage("&7Tanque defensor, absorbe daño"));
 golemLore.add(MessageUtils.getColoredMessage("&6» Resistencia extra"));
 golemLore.add(MessageUtils.getColoredMessage("&eClick para elegir"));
 golemMeta.setLore(golemLore);
 golem.setItemMeta(golemMeta);
 inv.setItem(10, golem);

 // ☃️ Muñeco de Nieve
 ItemStack snowman = new ItemStack(Material.SNOW_BALL);
 ItemMeta snowMeta = snowman.getItemMeta();
 snowMeta.setDisplayName(MessageUtils.getColoredMessage("&f✘ Muñeco de Nieve"));
 List<String> snowLore = new ArrayList<>();
 snowLore.add(MessageUtils.getColoredMessage("&7Ataca a distancia, molesta a mobs"));
 snowLore.add(MessageUtils.getColoredMessage("&6» Rango alto"));
 snowLore.add(MessageUtils.getColoredMessage("&eClick para elegir"));
 snowMeta.setLore(snowLore);
 snowman.setItemMeta(snowMeta);
 inv.setItem(12, snowman);

 // Mostrar el menú al jugador
 player.openInventory(inv);
 }

