package mp.managers;

import mp.plugin1.MiPlugin;
import mp.utils.MessageUtils;
import org.bukkit.Bukkit;
import org.bukkit.Location;
import org.bukkit.World;
import org.bukkit.entity.*;
import org.bukkit.inventory.ItemStack;
import org.bukkit.Material;
import org.bukkit.potion.PotionEffect;
import org.bukkit.potion.PotionEffectType;
import org.bukkit.scheduler.BukkitRunnable;

import java.util.Random;
import java.util.HashMap;
import java.util.Map;
import java.util.UUID;

public class WaveManager {
 private final MiPlugin plugin;
 private int currentWave = 0;
 private int zombiesAlive = 0;
 private boolean isWaveActive = false;
 private BukkitRunnable waveTask;
 private final Random random = new Random();
 // Control de elecciones de aliados por jugador (máx 2 por tipo)
 private final Map<UUID, Map<String, Integer>> allyEggChoices = new HashMap<>();

 public WaveManager(MiPlugin plugin) {
 this.plugin = plugin;
 }

 public void startWaveSystem() {
 if (isWaveActive) {
 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&c¡Ya hay una oleada activa!"));
 return;
 }
 currentWave = 0;
 zombiesAlive = 0;
 isWaveActive = true;
 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&a¡El juego de oleadas ha comenzado!"));
 startNextWave();
 }

 private void startNextWave() {
 currentWave++;
 isWaveActive = true;
 int mobCount = 5 + (currentWave * 2);

 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&c&lOLEADA " + currentWave + " &8» &7¡Ha comenzado!"));

 // Cada 3 oleadas, abrir menú de aliado a todos los jugadores
 if (currentWave % 3 == 0) {
 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&a¡Es hora de elegir un aliado especial!"));
 Bukkit.getScheduler().runTaskLater(plugin, () -> {
 for (Player player : Bukkit.getOnlinePlayers()) {
 plugin.getMenuInventoryManager().openAllyEggMenu(player);
 }
 }, 60L); // Esperar 3 segundos para dar tiempo a que los jugadores se preparen
 }

 for (Player player : Bukkit.getOnlinePlayers()) {
 spawnWaveMobs(player, mobCount);
 }

 // Finaliza la oleada después de 3 minutos si quedan mobs vivos
 if (waveTask != null) waveTask.cancel();
 waveTask = new BukkitRunnable() {
 @Override
 public void run() {
 // Limpiar solo enemigos (Monsters) que no sean aliados ni aldeanos
 for (World world : Bukkit.getWorlds()) {
 for (Entity entity : world.getEntities()) {
 if (entity instanceof Villager) continue;
 if (entity instanceof Player) continue;
 if (entity.getCustomName() != null && entity.getCustomName().contains("Aliado")) continue;
 if (entity instanceof Monster) {
 entity.remove();
 }
 }
 }
 if (zombiesAlive > 0) {
 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&eLa oleada terminó por tiempo. Eliminando mobs restantes..."));
 for (World world : Bukkit.getWorlds()) {
 for (Entity entity : world.getEntities()) {
 if (entity instanceof Monster && entity.getCustomName() != null && entity.getCustomName().contains("Oleada")) {
 entity.remove();
 }
 }
 }
 zombiesAlive = 0;
 }
 isWaveActive = false;
 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&a¡Oleada " + currentWave + " completada!"));
 // Iniciar siguiente oleada tras 10 segundos
 new BukkitRunnable() {
 @Override
 public void run() {
 startNextWave();
 }
 }.runTaskLater(plugin, 20 * 10);
 }
 };
 waveTask.runTaskLater(plugin, 20 * 180); // 3 minutos
 }

 private void spawnWaveMobs(Player player, int count) {
 World world = player.getWorld();
 Location playerLoc = player.getLocation();

 for (int i = 0; i < count; i++) {
 double angle = random.nextDouble() * 2 * Math.PI;
 double radius = random.nextDouble() * 5 + 5;
 double x = playerLoc.getX() + radius * Math.cos(angle);
 double z = playerLoc.getZ() + radius * Math.sin(angle);
 Location spawnLoc = new Location(world, x, playerLoc.getY(), z);
 spawnLoc.setY(world.getHighestBlockYAt(spawnLoc));

 if (currentWave <= 3) {
 Zombie zombie = (Zombie) world.spawnEntity(spawnLoc, EntityType.ZOMBIE);
 zombie.setCustomName(MessageUtils.getColoredMessage("&cZombie Oleada " + currentWave));
 zombie.setCustomNameVisible(true);
 zombiesAlive++;
 addRandomDrop(zombie);
 } else {
 // Jefe cada 5 oleadas
 if (currentWave % 5 == 0 && i == 0) {
 Zombie boss = (Zombie) world.spawnEntity(spawnLoc, EntityType.ZOMBIE);
 boss.setCustomName(MessageUtils.getColoredMessage("&6&lJEFE Zombi Supremo Oleada " + currentWave));
 boss.setCustomNameVisible(true);
 boss.setMaxHealth(80.0);
 boss.setHealth(80.0);
 boss.getEquipment().setHelmet(new ItemStack(Material.DIAMOND_HELMET));
 boss.getEquipment().setChestplate(new ItemStack(Material.DIAMOND_CHESTPLATE));
 boss.getEquipment().setItemInHand(new ItemStack(Material.DIAMOND_SWORD));
 boss.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 180, 2));
 boss.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 180, 1));
 zombiesAlive++;
 addRandomDrop(boss);
 continue;
 }

 int mobType = random.nextInt(4);
 switch (mobType) {
 case 0: // Zombi con armadura y arma
 Zombie zombieGuerrero = (Zombie) world.spawnEntity(spawnLoc, EntityType.ZOMBIE);
 zombieGuerrero.setCustomName(MessageUtils.getColoredMessage("&cZombie Guerrero Oleada " + currentWave));
 zombieGuerrero.setCustomNameVisible(true);
 zombieGuerrero.getEquipment().setHelmet(new ItemStack(Material.IRON_HELMET));
 zombieGuerrero.getEquipment().setChestplate(new ItemStack(Material.IRON_CHESTPLATE));
 zombieGuerrero.getEquipment().setItemInHand(new ItemStack(Material.IRON_SWORD));
 zombieGuerrero.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 180, 1));
 zombiesAlive++;
 addRandomDrop(zombieGuerrero);
 break;
 case 1: // Esqueleto con arco
 Skeleton skeletonArcher = (Skeleton) world.spawnEntity(spawnLoc, EntityType.SKELETON);
 skeletonArcher.setCustomName(MessageUtils.getColoredMessage("&7Esqueleto Arquero Oleada " + currentWave));
 skeletonArcher.setCustomNameVisible(true);
 skeletonArcher.getEquipment().setHelmet(new ItemStack(Material.CHAINMAIL_HELMET));
 skeletonArcher.getEquipment().setItemInHand(new ItemStack(Material.BOW));
 skeletonArcher.addPotionEffect(new PotionEffect(PotionEffectType.SPEED, 20 * 180, 1));
 zombiesAlive++;
 addRandomDrop(skeletonArcher);
 break;
 case 2: // Creeper
 Creeper creeper = (Creeper) world.spawnEntity(spawnLoc, EntityType.CREEPER);
 creeper.setCustomName(MessageUtils.getColoredMessage("&aCreeper Explosivo Oleada " + currentWave));
 creeper.setCustomNameVisible(true);
 zombiesAlive++;
 addRandomDrop(creeper);
 break;
 case 3: // Bruja
 Witch witch = (Witch) world.spawnEntity(spawnLoc, EntityType.WITCH);
 witch.setCustomName(MessageUtils.getColoredMessage("&5Bruja Maligna Oleada " + currentWave));
 witch.setCustomNameVisible(true);
 zombiesAlive++;
 addRandomDrop(witch);
 break;
 }
 }
 }
 }

 // Añade drops personalizados a los mobs hostiles
 private void addRandomDrop(LivingEntity entity) {
 int dropType = random.nextInt(100);
 ItemStack drop = null;
 if (dropType < 50) { // 50% comida
 Material[] foods = {Material.BREAD, Material.COOKED_BEEF, Material.COOKED_CHICKEN, Material.GOLDEN_APPLE};
 drop = new ItemStack(foods[random.nextInt(foods.length)]);
 } else if (dropType < 80) { // 30% poción
 drop = new ItemStack(Material.POTION, 1, (short) (8229 + random.nextInt(3))); // Regeneración, fuerza, velocidad
 } else { // 20% manzana dorada
 drop = new ItemStack(Material.GOLDEN_APPLE);
 }
 if (drop != null) {
 entity.getEquipment().setItemInHandDropChance(0.0f);
 entity.getEquipment().setHelmetDropChance(0.0f);
 entity.getEquipment().setChestplateDropChance(0.0f);
 entity.getEquipment().setLeggingsDropChance(0.0f);
 entity.getEquipment().setBootsDropChance(0.0f);
 entity.getEquipment().setItemInHand(null);
 entity.getEquipment().setHelmet(null);
 entity.getEquipment().setChestplate(null);
 entity.getEquipment().setLeggings(null);
 entity.getEquipment().setBoots(null);
 entity.getEquipment().setItemInHand(drop);
 entity.getEquipment().setItemInHandDropChance(1.0f);
 }
 }

 public void decrementZombiesAlive() {
 zombiesAlive--;
 if (zombiesAlive <= 0 && isWaveActive) {
 isWaveActive = false;
 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&a¡Oleada " + currentWave + " completada!"));
 if (waveTask != null) waveTask.cancel();
 // Iniciar siguiente oleada tras 10 segundos
 new BukkitRunnable() {
 @Override
 public void run() {
 startNextWave();
 }
 }.runTaskLater(plugin, 20 * 10);
 }
 }

 public void stopWaveSystem() {
 if (waveTask != null) waveTask.cancel();
 isWaveActive = false;
 zombiesAlive = 0;
 currentWave = 0;
 Bukkit.broadcastMessage(MessageUtils.getColoredMessage("&c¡El juego de oleadas ha sido detenido!"));
 }

 // Métodos para controlar elecciones de huevos aliados
 public boolean canChooseAllyEgg(Player player, String mobType) {
 UUID uuid = player.getUniqueId();
 allyEggChoices.putIfAbsent(uuid, new HashMap<>());
 Map<String, Integer> choices = allyEggChoices.get(uuid);
 return choices.getOrDefault(mobType, 0) < 2;
 }

 public void registerAllyEggChoice(Player player, String mobType) {
 UUID uuid = player.getUniqueId();
 allyEggChoices.putIfAbsent(uuid, new HashMap<>());
 Map<String, Integer> choices = allyEggChoices.get(uuid);
 choices.put(mobType, choices.getOrDefault(mobType, 0) + 1);
 }
}

