 public void spawnAlly(Player player, String allyType) {
 Location spawnLocation = player.getLocation().add(2, 0, 2); // Spawn cerca del jugador
 spawnLocation.setY(player.getWorld().getHighestBlockYAt(spawnLocation));

 switch (allyType.toLowerCase()) {
 case "golem":
 // Spawnear Gólem de Hierro
 IronGolem golem = (IronGolem) player.getWorld().spawnEntity(spawnLocation, EntityType.IRON_GOLEM);
 golem.setCustomName(MessageUtils.getColoredMessage("&f✘ &7Aliado de " + player.getName()));
 golem.setCustomNameVisible(true);

 // Configurar vida limitada (300 HP en lugar de 100 estándar)
 golem.setMaxHealth(300.0);
 golem.setHealth(300.0);

 // Efectos especiales
 golem.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 300, 1)); // 5 min de resistencia
 golem.addPotionEffect(new PotionEffect(PotionEffectType.REGENERATION, 20 * 60, 0)); // 1 min de regeneración

 // Hacer que siga al jugador (será el target más cercano a proteger)
 // En Bukkit 1.8, los gólems naturalmente protegen a los jugadores cercanos

 // Mensaje y efectos
 player.sendMessage(MessageUtils.getColoredMessage("&a&l➤ &f¡Tu Gólem Guardián ha aparecido!"));
 player.sendMessage(MessageUtils.getColoredMessage("&7» &fVida: &a300 HP &7| Duración: &e5 minutos"));
 player.getWorld().strikeLightningEffect(spawnLocation);
 break;

 case "snowman":
 // Spawnear Muñeco de Nieve
 Snowman snowman = (Snowman) player.getWorld().spawnEntity(spawnLocation, EntityType.SNOWMAN);
 snowman.setCustomName(MessageUtils.getColoredMessage("&b✘ &7Aliado de " + player.getName()));
 snowman.setCustomNameVisible(true);

 // Configurar vida limitada (50 HP en lugar de 4 estándar)
 snowman.setMaxHealth(50.0);
 snowman.setHealth(50.0);

 // Efectos especiales para mejor combate
 snowman.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 300, 1)); // Más daño
 snowman.addPotionEffect(new PotionEffect(PotionEffectType.SPEED, 20 * 300, 1)); // Más velocidad
 snowman.addPotionEffect(new PotionEffect(PotionEffectType.REGENERATION, 20 * 30, 0)); // Regeneración por 30s

 // Mensaje y efectos
 player.sendMessage(MessageUtils.getColoredMessage("&b&l➤ &f¡Tu Arquero de Nieve ha aparecido!"));
 player.sendMessage(MessageUtils.getColoredMessage("&7» &fVida: &a50 HP &7| Duración: &e5 minutos"));

 // Efectos de nieve
 for (int i = 0; i < 10; i++) {
 player.getWorld().playEffect(spawnLocation.add(Math.random() * 2 - 1, 1, Math.random() * 2 - 1),
 org.bukkit.Effect.SNOW_SHOVEL, 1);
 }
 break;
 }

 // Programar eliminación automática después de 5 minutos
 Bukkit.getScheduler().runTaskLater(plugin, () -> {
 // Buscar y remover aliados del jugador que hayan expirado
 for (org.bukkit.entity.Entity entity : player.getWorld().getEntities()) {
 if (entity.getCustomName() != null &&
 entity.getCustomName().contains("Aliado de " + player.getName())) {
 entity.remove();
 player.sendMessage(MessageUtils.getColoredMessage("&6⚠ &7Tu aliado ha desaparecido por límite de tiempo"));
 }
 }
 }, 20 * 300); // 5 minutos = 300 segundos
 }

 // Este método es opcional, solo si tenés implementado un borde decorativo
 private void fillBorder(Inventory inv, Material material, short data) {
 ItemStack border = new ItemStack(material, 1, data);
 for (int i = 0; i < inv.getSize(); i++) {
 if (i < 9 || i >= 18 || i % 9 == 0 || i % 9 == 8) {
 inv.setItem(i, border);
 }
 }
 }
