+ // Verificar si hay aliados cerca (protección adicional)
+ for (Entity entity : exploder.getNearbyEntities(10, 10, 10)) {
+ if (entity.getCustomName() != null && entity.getCustomName().contains("Aliado")) {
+ event.blockList().clear();
+
+ // Informar sobre protección
+ for (Player player : exploder.getWorld().getPlayers()) {
+ if (player.getLocation().distance(exploder.getLocation()) <= 15) {
+ player.sendMessage(MessageUtils.getColoredMessage("&b&l✓ &7Zona de aliado protegida contra explosión"));
+ }
+ }
+ break;
+ }
+ }
+ }
 }
+
+ private void addEpicBossDrops(EntityDeathEvent event, Entity entity) {
+ // Drops épicos para jefes
+ if (random.nextInt(100) < 80) { // 80% chance
+ event.getDrops().add(mp.utils.itemUtils.generateEpicSword());
+ }
+ if (random.nextInt(100) < 60) { // 60% chance
+ event.getDrops().add(mp.utils.itemUtils.generateRandomEpicArmor());
+ }
+ if (random.nextInt(100) < 40) { // 40% chance
+ event.getDrops().add(mp.utils.itemUtils.generateSpecialItem("bow"));
+ }
+
+ // Siempre dar items de supervivencia
+ event.getDrops().add(new ItemStack(Material.GOLDEN_APPLE, 3 + random.nextInt(3)));
+ event.getDrops().add(new ItemStack(Material.DIAMOND, 2 + random.nextInt(2)));
+ event.getDrops().add(mp.utils.itemUtils.generateEsmeralditem(5 + random.nextInt(5)));
+ }
+
+ private void addEliteDrops(EntityDeathEvent event, Entity entity) {
+ // Drops mejorados para enemigos élite
+ if (random.nextInt(100) < 50) { // 50% chance
+ ItemStack[] possibleItems = {
+ mp.utils.itemUtils.generateEpicSword(),
+ mp.utils.itemUtils.generateRandomEpicArmor(),
+ mp.utils.itemUtils.generateSpecialItem("axe")
+ };
+ event.getDrops().add(possibleItems[random.nextInt(possibleItems.length)]);
+ }
+
+ // Items de supervivencia garantizados
+ event.getDrops().add(new ItemStack(Material.GOLDEN_APPLE, 1 + random.nextInt(2)));
+ event.getDrops().add(new ItemStack(Material.COOKED_BEEF, 3 + random.nextInt(3)));
+
+ if (random.nextInt(100) < 30) { // 30% chance de esmeralda épica
+ event.getDrops().add(mp.utils.itemUtils.generateEsmeralditem(2 + random.nextInt(3)));
+ }
+ }
+
+ private void addBasicDrops(EntityDeathEvent event, Entity entity) {
+ // Drops básicos pero mejorados
