public class ZombieListener implements Listener {
 private final MiPlugin plugin;
 private final Random random = new Random();

 public ZombieListener(MiPlugin plugin) {
 this.plugin = plugin;
 }

 @EventHandler
 public void onMobDeath(EntityDeathEvent event) {
 Entity entity = event.getEntity();
 String name = entity.getCustomName();
 if (name != null && name.toLowerCase().contains("oleada")) {
 plugin.getWaveManager().decrementZombiesAlive();
 }

 // Solo procesar mobs hostiles con nombres personalizados (de oleadas)
 if (!(entity instanceof Monster)) return;
 if (entity.getCustomName() == null) return;
 if (!entity.getCustomName().contains("Oleada")) return;

 // Limpiar drops por defecto y agregar drops épicos
 event.getDrops().clear();

 // Dar XP extra por matar enemigos de oleada
 event.setDroppedExp(event.getDroppedExp() * 2);

 // Drops épicos garantizados según el tipo de oleada
 int currentWave = plugin.getWaveManager().getCurrentWave();

 if (currentWave >= 5) {
 // Oleada 5+: Drops de jefe épicos
 addEpicBossDrops(event, entity);
 } else if (currentWave >= 4) {
 // Oleada 4+: Drops élite
 addEliteDrops(event, entity);
 } else {
 // Oleadas 1-3: Drops básicos mejorados
 addBasicDrops(event, entity);
 }
 }

 @EventHandler
 public void onVillagerDamage(EntityDamageByEntityEvent event) {
 if (event.getEntity() instanceof Villager) {
 // Cancelar cualquier daño a aldeanos, sin importar el atacante
 event.setCancelled(true);
 }
 }

 @EventHandler
 public void onVillagerTarget(EntityTargetLivingEntityEvent event) {
 if (event.getTarget() instanceof Villager) {
 // No cancelar el evento, así los aldeanos huyen y se asustan
 }
 }

 @EventHandler(priority = EventPriority.HIGH)
 public void onEntityExplode(EntityExplodeEvent event) {
 Entity exploder = event.getEntity();

 // Solo proteger contra explosiones de entidades de oleadas
 if (exploder.getCustomName() != null && exploder.getCustomName().contains("Oleada")) {
 // Cancelar daño a bloques pero mantener daño a entidades
 event.blockList().clear();

 // Mensaje informativo para jugadores cercanos
 for (Player player : exploder.getWorld().getPlayers()) {
 if (player.getLocation().distance(exploder.getLocation()) <= 20) {
 player.sendMessage(MessageUtils.getColoredMessage("&a&l✓ &7Aldea protegida contra explosión de &c" +
 exploder.getCustomName().replace("§", "&")));
 }
 }
 }

 // También proteger contra explosiones de creepers normales cerca de aliados
 if (exploder instanceof Creeper) {
 // Verificar si hay aliados cerca (protección adicional)
 for (Entity entity : exploder.getNearbyEntities(10, 10, 10)) {
 if (entity.getCustomName() != null && entity.getCustomName().contains("Aliado")) {
 event.blockList().clear();

 // Informar sobre protección
 for (Player player : exploder.getWorld().getPlayers()) {
 if (player.getLocation().distance(exploder.getLocation()) <= 15) {
 player.sendMessage(MessageUtils.getColoredMessage("&b&l✓ &7Zona de aliado protegida contra explosión"));
 }
 }
 break;
 }
 }
 }
 }

 private void addEpicBossDrops(EntityDeathEvent event, Entity entity) {
 // Drops épicos para jefes
 if (random.nextInt(100) < 80) { // 80% chance
 event.getDrops().add(mp.utils.itemUtils.generateEpicSword());
 }
 if (random.nextInt(100) < 60) { // 60% chance
 event.getDrops().add(mp.utils.itemUtils.generateRandomEpicArmor());
 }
 if (random.nextInt(100) < 40) { // 40% chance
 event.getDrops().add(mp.utils.itemUtils.generateSpecialItem("bow"));
 }

 // Siempre dar items de supervivencia
 event.getDrops().add(new ItemStack(Material.GOLDEN_APPLE, 3 + random.nextInt(3)));
 event.getDrops().add(new ItemStack(Material.DIAMOND, 2 + random.nextInt(2)));
 event.getDrops().add(mp.utils.itemUtils.generateEsmeralditem(5 + random.nextInt(5)));
 }

 private void addEliteDrops(EntityDeathEvent event, Entity entity) {
 // Drops mejorados para enemigos élite
 if (random.nextInt(100) < 50) { // 50% chance
 ItemStack[] possibleItems = {
 mp.utils.itemUtils.generateEpicSword(),
 mp.utils.itemUtils.generateRandomEpicArmor(),
 mp.utils.itemUtils.generateSpecialItem("axe")
 };
 event.getDrops().add(possibleItems[random.nextInt(possibleItems.length)]);
 }

 // Items de supervivencia garantizados
 event.getDrops().add(new ItemStack(Material.GOLDEN_APPLE, 1 + random.nextInt(2)));
 event.getDrops().add(new ItemStack(Material.COOKED_BEEF, 3 + random.nextInt(3)));

 if (random.nextInt(100) < 30) { // 30% chance de esmeralda épica
 event.getDrops().add(mp.utils.itemUtils.generateEsmeralditem(2 + random.nextInt(3)));
 }
 }

 private void addBasicDrops(EntityDeathEvent event, Entity entity) {
 // Drops básicos pero mejorados
 Material[] foods = {Material.BREAD, Material.COOKED_BEEF, Material.COOKED_CHICKEN, Material.GOLDEN_APPLE};
 event.getDrops().add(new ItemStack(foods[random.nextInt(foods.length)], 1 + random.nextInt(3)));

 // Chance de item épico ocasional
 if (random.nextInt(100) < 20) { // 20% chance
 if (random.nextBoolean()) {
 event.getDrops().add(mp.utils.itemUtils.generateRandomEpicArmor());
 } else {
 event.getDrops().add(mp.utils.itemUtils.generateEsmeralditem(1 + random.nextInt(2)));
 }
 }

 // Pociones ocasionales
 if (random.nextInt(100) < 40) { // 40% chance
 event.getDrops().add(new ItemStack(Material.POTION, 1, (short) (8229 + random.nextInt(3))));
 }
 }
