```
package mp.listeners;

import mp.plugin1.MiPlugin;
import mp.utils.MessageUtils;
import org.bukkit.event.Listener;
import org.bukkit.event.EventHandler;
import org.bukkit.event.EventPriority;
import org.bukkit.event.inventory.InventoryClickEvent;
import org.bukkit.event.inventory.InventoryDragEvent;
import org.bukkit.event.inventory.InventoryMoveItemEvent;
import org.bukkit.inventory.ItemStack;

public class MenuListener implements Listener {
 private final MiPlugin plugin;

 public MenuListener(MiPlugin plugin) {
 this.plugin = plugin;
 }

 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryClick(InventoryClickEvent event) {
 if (!(event.getWhoClicked() instanceof org.bukkit.entity.Player)) return;
 org.bukkit.entity.Player player = (org.bukkit.entity.Player) event.getWhoClicked();
 if (event.getInventory() == null) return;

 String title = event.getView().getTitle();
 if (title == null) return;

 // Detectar si es un menú del plugin
 boolean isPluginMenu = title.contains("Equipos Nórdicos Épicos") ||
 title.contains("Elige tu aliado especial") ||
 title.contains("Selección de Aliados") ||
 title.contains("Menú Principal") ||
 title.contains("MENÚ PRINCIPAL");

 if (isPluginMenu) {
 event.setCancelled(true); // SIEMPRE cancelar interacciones en menús del plugin

 if (event.getCurrentItem() == null || !event.getCurrentItem().hasItemMeta()) {
 return; // Click en slot vacío
 }
 }

 // MENÚ PRINCIPAL
 if (title.contains("MENÚ PRINCIPAL")) {
 ItemStack clicked = event.getCurrentItem();
 if (!clicked.getItemMeta().hasDisplayName()) return;

 String name = clicked.getItemMeta().getDisplayName();

 if (name.contains("INICIAR OLEADAS")) {
 if (!plugin.getWaveManager().isWaveActive()) {
 plugin.getWaveManager().startWaveSystem();
 player.sendMessage(MessageUtils.getColoredMessage("&a&l✓ &f¡Sistema de oleadas iniciado!"));
 } else {
 player.sendMessage(MessageUtils.getColoredMessage("&c&l✗ &f¡Ya hay oleadas activas!"));
 }
 player.closeInventory();
 } else if (name.contains("EQUIPOS NÓRDICOS")) {
 plugin.getMenuInventoryManager().openNorseEpicTeamsMenu(player);
 } else if (name.contains("DETENER OLEADAS")) {
 if (plugin.getWaveManager().isWaveActive()) {
 plugin.getWaveManager().stopWaveSystem();
 player.sendMessage(MessageUtils.getColoredMessage("&c&l✓ &f¡Sistema de oleadas detenido!"));
 } else {
 player.sendMessage(MessageUtils.getColoredMessage("&e&l⚠ &f¡No hay oleadas activas!"));
 }
 player.closeInventory();
 }
 }

 // Menú de Equipos Nórdicos
 if (title.contains("Equipos Nórdicos Épicos")) {
 ItemStack clicked = event.getCurrentItem();
 if (!clicked.getItemMeta().hasDisplayName()) return;

 String name = clicked.getItemMeta().getDisplayName();
 if (name.contains("Odín")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "odin");
 player.closeInventory();
 } else if (name.contains("Thor")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "thor");
 player.closeInventory();
 } else if (name.contains("Loki")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "loki");
 player.closeInventory();
 } else if (name.contains("Freyja")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "freyja");
 player.closeInventory();
 }
 }

 // Menú de Selección de Aliados
 else if (title.contains("Elige tu aliado especial")) {
 ItemStack clicked = event.getCurrentItem();
 if (!clicked.getItemMeta().hasDisplayName()) return;

 String name = clicked.getItemMeta().getDisplayName();

 if (name.contains("Gólem de Hierro")) {
 if (plugin.getWaveManager().canChooseAllyEgg(player, "golem")) {
 plugin.getMenuInventoryManager().spawnAlly(player, "golem");
 plugin.getWaveManager().registerAllyEggChoice(player, "golem");
 player.sendMessage(MessageUtils.getColoredMessage("&a✓ &f¡Has elegido un Gólem de Hierro como aliado!"));
 player.closeInventory();
 } else {
 player.sendMessage(MessageUtils.getColoredMessage("&c✗ &7Ya tienes el máximo de Gólems permitidos (2/2)"));
 }
 } else if (name.contains("Muñeco de Nieve")) {
 if (plugin.getWaveManager().canChooseAllyEgg(player, "snowman")) {
