 public static ItemStack[] generateApolloFullKit() {
 ItemStack[] kit = new ItemStack[5];
 // Arco Solar
 kit[0] = generateApolloKit();

 // Set Solar (Oro con resistencia al fuego y durabilidad)
 ItemStack helmet = new ItemStack(Material.GOLD_HELMET);
 ItemMeta helmetMeta = helmet.getItemMeta();
 helmetMeta.setDisplayName(MessageUtils.getColoredMessage("&6☀ &eCorona Solar &6☀"));
 List<String> helmetLore = new ArrayList<>();
 helmetLore.add(MessageUtils.getColoredMessage("&8▪ &7Bendecida por el sol"));
