```java:src/main/java/mp/listeners/MenuListener.java
package mp.listeners;

import mp.plugin1.MiPlugin;
import mp.utils.MessageUtils;
import org.bukkit.event.Listener;
import org.bukkit.event.EventHandler;
import org.bukkit.event.EventPriority;
import org.bukkit.event.inventory.*;
import org.bukkit.inventory.ItemStack;

public class MenuListener implements Listener {
 private final MiPlugin plugin;

 public MenuListener(MiPlugin plugin) {
 this.plugin = plugin;
 }

 // Detectar si un inventario es un menú del plugin
 private boolean isPluginMenu(String title) {
 if (title == null) return false;
 return title.contains("Equipos Nórdicos Épicos") ||
 title.contains("Elige tu aliado especial") ||
 title.contains("Selección de Aliados") ||
 title.contains("Menú Principal") ||
 title.contains("MENÚ PRINCIPAL") ||
 title.contains("TUS ALIADOS") ||
 title.contains("ESTADO ACTUAL") ||
 title.contains("Información de Aliados");
 }

 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryClick(InventoryClickEvent event) {
 if (!(event.getWhoClicked() instanceof org.bukkit.entity.Player)) return;
 org.bukkit.entity.Player player = (org.bukkit.entity.Player) event.getWhoClicked();
 if (event.getInventory() == null) return;

 String title = event.getView().getTitle();

 // PROTECCIÓN TOTAL para menús del plugin
 if (isPluginMenu(title)) {
 event.setCancelled(true); // SIEMPRE cancelar TODAS las interacciones

 // Si no hay item clickeado o no tiene metadata, no hacer nada
 if (event.getCurrentItem() == null || !event.getCurrentItem().hasItemMeta()) {
 return;
 }

 // Solo procesar clicks funcionales en items con nombres
 if (!event.getCurrentItem().getItemMeta().hasDisplayName()) {
 return;
 }
 }

 // ============= MENÚ PRINCIPAL =============
 if (title.contains("MENÚ PRINCIPAL")) {
 String name = event.getCurrentItem().getItemMeta().getDisplayName();

 if (name.contains("INICIAR OLEADAS")) {
 if (!plugin.getWaveManager().isWaveActive()) {
 plugin.getWaveManager().startWaveSystem();
 player.sendMessage(MessageUtils.getColoredMessage("&a&l✓ &f¡Sistema de oleadas iniciado exitosamente!"));
 // Reabrir menú actualizado para mostrar nuevos botones
 plugin.getMenuInventoryManager().openMainInventory(player);
 } else {
 player.sendMessage(MessageUtils.getColoredMessage("&c&l✗ &f¡Ya hay oleadas activas en este momento!"));
 }
 } else if (name.contains("EQUIPOS NÓRDICOS")) {
 plugin.getMenuInventoryManager().openNorseEpicTeamsMenu(player);
 } else if (name.contains("DETENER OLEADAS")) {
 if (plugin.getWaveManager().isWaveActive()) {
 plugin.getWaveManager().stopWaveSystem();
 player.sendMessage(MessageUtils.getColoredMessage("&c&l✓ &f¡Sistema de oleadas detenido correctamente!"));
 // Reabrir menú actualizado
 plugin.getMenuInventoryManager().openMainInventory(player);
 } else {
 player.sendMessage(MessageUtils.getColoredMessage("&e&l⚠ &f¡No hay oleadas activas para detener!"));
 }
 }
 // Los items informativos (Estado, Aliados) no hacen nada al clickear
 }

 // ============= MENÚ DE EQUIPOS NÓRDICOS =============
 else if (title.contains("Equipos Nórdicos Épicos")) {
 String name = event.getCurrentItem().getItemMeta().getDisplayName();

 if (name.contains("Odín")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "odin");
 player.closeInventory();
 } else if (name.contains("Thor")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "thor");
 player.closeInventory();
 } else if (name.contains("Loki")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "loki");
 player.closeInventory();
 } else if (name.contains("Freyja")) {
 plugin.getMenuInventoryManager().giveNorseKit(player, "freyja");
 player.closeInventory();
 }
 }

 // ============= MENÚ DE SELECCIÓN DE ALIADOS =============
 else if (title.contains("Elige tu aliado especial")) {
 String name = event.getCurrentItem().getItemMeta().getDisplayName();

 if (name.contains("Gólem de Hierro")) {
 if (plugin.getWaveManager().canChooseAllyEgg(player, "golem")) {
 plugin.getMenuInventoryManager().spawnAlly(player, "golem");
 plugin.getWaveManager().registerAllyEggChoice(player, "golem");
 player.sendMessage(MessageUtils.getColoredMessage("&a&l✓ &f¡Has elegido un &7Gólem de Hierro &fcomo aliado!"));
 // Reabrir menú actualizado para mostrar contadores actualizados
 plugin.getMenuInventoryManager().openAllyEggMenu(player);
 } else {
 player.sendMessage(MessageUtils.getColoredMessage("&c&l✗ &7Ya tienes el máximo de Gólems permitidos (2/2)"));
 }
 } else if (name.contains("Muñeco de Nieve")) {
 if (plugin.getWaveManager().canChooseAllyEgg(player, "snowman")) {
 plugin.getMenuInventoryManager().spawnAlly(player, "snowman");
 plugin.getWaveManager().registerAllyEggChoice(player, "snowman");
 player.sendMessage(MessageUtils.getColoredMessage("&b&l✓ &f¡Has elegido un &fMuñeco de Nieve &fcomo aliado!"));
 // Reabrir menú actualizado para mostrar contadores actualizados
 plugin.getMenuInventoryManager().openAllyEggMenu(player);
 } else {
 player.sendMessage(MessageUtils.getColoredMessage("&c&l✗ &7Ya tienes el máximo de Muñecos de Nieve permitidos (2/2)"));
 }
 }
 // Item informativo central no hace nada al clickear
 }
 }

 // PROTECCIÓN TOTAL: Prevenir arrastrar items
 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryDrag(InventoryDragEvent event) {
 if (!(event.getWhoClicked() instanceof org.bukkit.entity.Player)) return;

 String title = event.getView().getTitle();
 if (isPluginMenu(title)) {
 event.setCancelled(true);
 }
 }

 // PROTECCIÓN TOTAL: Prevenir mover items automáticamente
 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryMoveItem(InventoryMoveItemEvent event) {
 String sourceTitle = event.getSource().getTitle();
 String destTitle = event.getDestination().getTitle();

 if (isPluginMenu(sourceTitle) || isPluginMenu(destTitle)) {
 event.setCancelled(true);
 }
 }

 // PROTECCIÓN ADICIONAL: Prevenir shift-click
 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryShiftClick(InventoryClickEvent event) {
 if (!(event.getWhoClicked() instanceof org.bukkit.entity.Player)) return;

 String title = event.getView().getTitle();
 if (isPluginMenu(title) && event.isShiftClick()) {
 event.setCancelled(true);
 }
 }

 // PROTECCIÓN ADICIONAL: Prevenir doble click
 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryDoubleClick(InventoryClickEvent event) {
 if (!(event.getWhoClicked() instanceof org.bukkit.entity.Player)) return;

 String title = event.getView().getTitle();
 if (isPluginMenu(title) && event.getClick().name().contains("DOUBLE")) {
 event.setCancelled(true);
 }
 }

 // PROTECCIÓN ADICIONAL: Prevenir drop de items
 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryDrop(InventoryClickEvent event) {
 if (!(event.getWhoClicked() instanceof org.bukkit.entity.Player)) return;

 String title = event.getView().getTitle();
 if (isPluginMenu(title) && (event.getClick() == ClickType.DROP || event.getClick() == ClickType.CONTROL_DROP)) {
 event.setCancelled(true);
 }
 }

 // PROTECCIÓN ADICIONAL: Prevenir cerrar inventario con items en cursor
 @EventHandler(priority = EventPriority.HIGHEST)
 public void onInventoryClose(InventoryCloseEvent event) {
 if (!(event.getPlayer() instanceof org.bukkit.entity.Player)) return;
 org.bukkit.entity.Player player = (org.bukkit.entity.Player) event.getPlayer();

 String title = event.getView().getTitle();
 if (isPluginMenu(title)) {
 // Si el jugador tiene un item en el cursor al cerrar un menú del plugin, devolverlo
 ItemStack cursor = event.getPlayer().getItemOnCursor();
 if (cursor != null && cursor.getType() != org.bukkit.Material.AIR) {
 player.getInventory().addItem(cursor);
 player.setItemOnCursor(null);
 }
 }
 }
}

