 public void giveNorseKit(Player player, String team) {
 // Limpiar inventario y efectos anteriores
 player.getInventory().clear();
 for (PotionEffect effect : player.getActivePotionEffects()) {
 player.removePotionEffect(effect.getType());
 }

 switch (team.toLowerCase()) {
 case "odin":
 // Set completo de Odín
 ItemStack[] odinSet = mp.utils.itemUtils.generateOdinFullKit();
 // Equipar armadura
 player.getInventory().setHelmet(odinSet[1]);
 player.getInventory().setChestplate(odinSet[2]);
 player.getInventory().setLeggings(odinSet[3]);
 player.getInventory().setBoots(odinSet[4]);
 // Dar arma principal
 player.getInventory().setItem(0, odinSet[0]);

 // Efectos mejorados de Odín
 player.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 300, 2));
 player.addPotionEffect(new PotionEffect(PotionEffectType.NIGHT_VISION, 20 * 300, 0));
 player.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.REGENERATION, 20 * 300, 0));

 // Efectos visuales y sonido
 player.getWorld().strikeLightningEffect(player.getLocation());
 player.getWorld().strikeLightningEffect(player.getLocation().add(1, 0, 1));
 player.getWorld().strikeLightningEffect(player.getLocation().add(-1, 0, -1));
 player.playSound(player.getLocation(), org.bukkit.Sound.WITHER_SPAWN, 1.0f, 0.5f);
 player.sendMessage(MessageUtils.getColoredMessage("&8&l[&f&lOdín&8&l] &b✧ &f¡El poder del Padre de Todos fluye por tus venas!"));
 player.sendMessage(MessageUtils.getColoredMessage("&b&l➤ &7Has recibido el set completo de Odín"));
 break;

 case "thor":
 // Set completo de Thor
 ItemStack[] thorSet = mp.utils.itemUtils.generateAresFullKit();
 // Equipar armadura
 player.getInventory().setHelmet(thorSet[1]);
 player.getInventory().setChestplate(thorSet[2]);
 player.getInventory().setLeggings(thorSet[3]);
 player.getInventory().setBoots(thorSet[4]);
 // Dar arma principal y extras
 player.getInventory().setItem(0, thorSet[0]); // Hacha principal
 player.getInventory().setItem(1, new ItemStack(Material.BOW)); // Arco secundario
 player.getInventory().setItem(8, new ItemStack(Material.ARROW, 64)); // Flechas

 // Efectos mejorados de Thor
 player.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 300, 2));
 player.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.FIRE_RESISTANCE, 20 * 300, 0));
 player.addPotionEffect(new PotionEffect(PotionEffectType.SPEED, 20 * 300, 0));

 // Efectos visuales y sonido
 for (int i = 0; i < 4; i++) {
 final int count = i;
 Bukkit.getScheduler().runTaskLater(plugin, () -> {
 player.getWorld().strikeLightningEffect(player.getLocation());
 player.playSound(player.getLocation(), org.bukkit.Sound.ENDERDRAGON_GROWL, 1.0f, 0.5f);
 }, i * 5L);
 }
 player.sendMessage(MessageUtils.getColoredMessage("&e&l[&6&lThor&e&l] &c✯ &e¡El poder del trueno recorre tu cuerpo!"));
 player.sendMessage(MessageUtils.getColoredMessage("&c&l➤ &7Has recibido el set completo de Thor"));
 break;

 case "loki":
 // Set completo de Loki
 ItemStack[] lokiSet = mp.utils.itemUtils.generateHermesFullKit();
 // Equipar armadura
 player.getInventory().setHelmet(lokiSet[1]);
 player.getInventory().setChestplate(lokiSet[2]);
 player.getInventory().setLeggings(lokiSet[3]);
 player.getInventory().setBoots(lokiSet[4]);
 // Dar armas y items especiales
 player.getInventory().setItem(0, lokiSet[0]); // Daga principal
 player.getInventory().setItem(1, new ItemStack(Material.ENDER_PEARL, 16)); // Perlas de ender
 player.getInventory().setItem(2, new ItemStack(Material.POTION, 3, (short)16398)); // Pociones de invisibilidad
 player.getInventory().setItem(8, new ItemStack(Material.GOLDEN_APPLE, 3)); // Manzanas doradas para escape

 // Efectos mejorados de Loki
 player.addPotionEffect(new PotionEffect(PotionEffectType.SPEED, 20 * 300, 2));
 player.addPotionEffect(new PotionEffect(PotionEffectType.JUMP, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.NIGHT_VISION, 20 * 300, 0));

 // Efectos visuales y sonido
 for (int i = 0; i < 8; i++) {
 player.getWorld().playEffect(player.getLocation().add(0, i/2.0, 0), org.bukkit.Effect.SMOKE, 4);
 }
 player.playSound(player.getLocation(), Sound.ENDERMAN_TELEPORT, 1.0f, 0.5f);
 player.sendMessage(MessageUtils.getColoredMessage("&2&l[&a&lLoki&2&l] &a❈ &2¡Las sombras te abrazan, maestro del engaño!"));
 player.sendMessage(MessageUtils.getColoredMessage("&a&l➤ &7Has recibido el set completo de Loki"));
 break;

 case "freyja":
 // Set completo de Freyja
 ItemStack[] freyjaSet = mp.utils.itemUtils.generateApolloFullKit();
 // Equipar armadura
 player.getInventory().setHelmet(freyjaSet[1]);
 player.getInventory().setChestplate(freyjaSet[2]);
 player.getInventory().setLeggings(freyjaSet[3]);
 player.getInventory().setBoots(freyjaSet[4]);
 // Dar armas y items especiales
 player.getInventory().setItem(0, freyjaSet[0]); // Arco principal
 player.getInventory().setItem(1, new ItemStack(Material.DIAMOND_SWORD)); // Espada de respaldo
 player.getInventory().setItem(7, new ItemStack(Material.GOLDEN_APPLE, 5)); // Manzanas doradas
 player.getInventory().setItem(8, new ItemStack(Material.ARROW, 64)); // Flechas

 // Efectos mejorados de Freyja
 player.addPotionEffect(new PotionEffect(PotionEffectType.REGENERATION, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.ABSORPTION, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 300, 0));
 player.addPotionEffect(new PotionEffect(PotionEffectType.FIRE_RESISTANCE, 20 * 300, 0));

 // Efectos visuales y sonido
 for (int i = 0; i < 3; i++) {
 player.getWorld().playEffect(player.getLocation(), org.bukkit.Effect.HEART, 1);
 player.getWorld().playEffect(player.getLocation().add(Math.random()*2-1, 1, Math.random()*2-1), org.bukkit.Effect.HEART, 1);
 }
 player.playSound(player.getLocation(), Sound.LEVEL_UP, 1.0f, 1.2f);
 player.sendMessage(MessageUtils.getColoredMessage("&5&l[&d&lFreyja&5&l] &d❀ &5¡La belleza y el poder de Vanaheim te protegen!"));
 player.sendMessage(MessageUtils.getColoredMessage("&d&l➤ &7Has recibido el set completo de Freyja"));
 break;
 }
 }

 public void spawnAlly(Player player, String allyType) {
 Location spawnLocation = player.getLocation().add(2, 0, 2); // Spawn cerca del jugador
 spawnLocation.setY(player.getWorld().getHighestBlockYAt(spawnLocation));

 switch (allyType.toLowerCase()) {
 case "golem":
 // Spawnear Gólem de Hierro
 IronGolem golem = (IronGolem) player.getWorld().spawnEntity(spawnLocation, EntityType.IRON_GOLEM);
 golem.setCustomName(MessageUtils.getColoredMessage("&f✘ &7Aliado de " + player.getName()));
 golem.setCustomNameVisible(true);

 // Configurar vida limitada (300 HP en lugar de 100 estándar)
 golem.setMaxHealth(300.0);
 golem.setHealth(300.0);

 // Efectos especiales
 golem.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 300, 1)); // 5 min de resistencia
 golem.addPotionEffect(new PotionEffect(PotionEffectType.REGENERATION, 20 * 60, 0)); // 1 min de regeneración

 // Hacer que siga al jugador (será el target más cercano a proteger)
 // En Bukkit 1.8, los gólems naturalmente protegen a los jugadores cercanos

 // Mensaje y efectos
 player.sendMessage(MessageUtils.getColoredMessage("&a&l➤ &f¡Tu Gólem Guardián ha aparecido!"));
 player.sendMessage(MessageUtils.getColoredMessage("&7» &fVida: &a300 HP &7| Duración: &e5 minutos"));
 player.getWorld().strikeLightningEffect(spawnLocation);
 break;

 case "snowman":
 // Spawnear Muñeco de Nieve
 Snowman snowman = (Snowman) player.getWorld().spawnEntity(spawnLocation, EntityType.SNOWMAN);
 snowman.setCustomName(MessageUtils.getColoredMessage("&b✘ &7Aliado de " + player.getName()));
 snowman.setCustomNameVisible(true);

 // Configurar vida limitada (50 HP en lugar de 4 estándar)
 snowman.setMaxHealth(50.0);
 snowman.setHealth(50.0);

 // Efectos especiales para mejor combate
 snowman.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 300, 1)); // Más daño
 snowman.addPotionEffect(new PotionEffect(PotionEffectType.SPEED, 20 * 300, 1)); // Más velocidad
 snowman.addPotionEffect(new PotionEffect(PotionEffectType.REGENERATION, 20 * 30, 0)); // Regeneración por 30s

 // Mensaje y efectos
 player.sendMessage(MessageUtils.getColoredMessage("&b&l➤ &f¡Tu Arquero de Nieve ha aparecido!"));
 player.sendMessage(MessageUtils.getColoredMessage("&7» &fVida: &a50 HP &7| Duración: &e5 minutos"));

 // Efectos de nieve
 for (int i = 0; i < 10; i++) {
 player.getWorld().playEffect(spawnLocation.add(Math.random() * 2 - 1, 1, Math.random() * 2 - 1),
 org.bukkit.Effect.SNOW_SHOVEL, 1);
 }
 break;
 }

 // Programar eliminación automática después de 5 minutos
 Bukkit.getScheduler().runTaskLater(plugin, () -> {
 // Buscar y remover aliados del jugador que hayan expirado
 for (org.bukkit.entity.Entity entity : player.getWorld().getEntities()) {
 if (entity.getCustomName() != null &&
 entity.getCustomName().contains("Aliado de " + player.getName())) {
 entity.remove();
 player.sendMessage(MessageUtils.getColoredMessage("&6⚠ &7Tu aliado ha desaparecido por límite de tiempo"));
 }
 }
 }, 20 * 300); // 5 minutos = 300 segundos
 }

 // Este método es opcional, solo si tenés implementado un borde decorativo
 private void fillBorder(Inventory inv, Material material, short data) {
 ItemStack border = new ItemStack(material, 1, data);
 for (int i = 0; i < inv.getSize(); i++) {
 if (i < 9 || i >= 18 || i % 9 == 0 || i % 9 == 8) {
 inv.setItem(i, border);
 }
 }
 }
}

