+ break;
+
+ case "snowman":
+ Snowman snowman = (Snowman) player.getWorld().spawnEntity(spawnLocation, EntityType.SNOWMAN);
+ snowman.setCustomName(MessageUtils.getColoredMessage("&b&l❄ &7Vigilante de " + player.getName()));
