 private void fillBorder(Inventory inv, Material material, short data) {
 ItemStack border = new ItemStack(material, 1, data);
 for (int i = 0; i < inv.getSize(); i++) {
 if (i < 9 || i >= 18 || i % 9 == 0 || i % 9 == 8) {
 inv.setItem(i, border);
 }
 }
 }
