 public void giveNorseKit(Player player, String team) {
 player.getInventory().clear();
 for (PotionEffect effect : player.getActivePotionEffects()) {
 player.removePotionEffect(effect.getType());
 }
 switch (team.toLowerCase()) {
 case "odin":
 ItemStack[] odinSet = ItemUtils.generateOdinFullKit();
 player.getInventory().setHelmet(odinSet[1]);
 player.getInventory().setChestplate(odinSet[2]);
 player.getInventory().setLeggings(odinSet[3]);
 player.getInventory().setBoots(odinSet[4]);
 player.getInventory().setItem(0, odinSet[0]);
 player.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 300, 2));
 player.addPotionEffect(new PotionEffect(PotionEffectType.NIGHT_VISION, 20 * 300, 0));
 player.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.REGENERATION, 20 * 300, 0));
 player.getWorld().strikeLightningEffect(player.getLocation());
 player.getWorld().strikeLightningEffect(player.getLocation().add(1, 0, 1));
 player.getWorld().strikeLightningEffect(player.getLocation().add(-1, 0, -1));
 player.playSound(player.getLocation(), org.bukkit.Sound.WITHER_SPAWN, 1.0f, 0.5f);
 player.sendMessage(MessageUtils.getColoredMessage("&8&l[&f&lOdín&8&l] &b✧ &f¡El poder del Padre de Todos fluye por tus venas!"));
 player.sendMessage(MessageUtils.getColoredMessage("&b&l➤ &7Has recibido el set completo de Odín"));
 break;
 case "thor":
 ItemStack[] thorSet = ItemUtils.generateAresFullKit();
 player.getInventory().setHelmet(thorSet[1]);
 player.getInventory().setChestplate(thorSet[2]);
 player.getInventory().setLeggings(thorSet[3]);
 player.getInventory().setBoots(thorSet[4]);
 player.getInventory().setItem(0, thorSet[0]);
 player.getInventory().setItem(1, new ItemStack(Material.BOW));
 player.getInventory().setItem(8, new ItemStack(Material.ARROW, 64));
 player.addPotionEffect(new PotionEffect(PotionEffectType.INCREASE_DAMAGE, 20 * 300, 2));
 player.addPotionEffect(new PotionEffect(PotionEffectType.DAMAGE_RESISTANCE, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.FIRE_RESISTANCE, 20 * 300, 0));
 player.addPotionEffect(new PotionEffect(PotionEffectType.SPEED, 20 * 300, 0));
 for (int i = 0; i < 4; i++) {
 final int count = i;
 Bukkit.getScheduler().runTaskLater(plugin, () -> {
 player.getWorld().strikeLightningEffect(player.getLocation());
 player.playSound(player.getLocation(), org.bukkit.Sound.ENDERDRAGON_GROWL, 1.0f, 0.5f);
 }, i * 5L);
 }
 player.sendMessage(MessageUtils.getColoredMessage("&e&l[&6&lThor&e&l] &c✯ &e¡El poder del trueno recorre tu cuerpo!"));
 player.sendMessage(MessageUtils.getColoredMessage("&c&l➤ &7Has recibido el set completo de Thor"));
 break;
 case "loki":
 ItemStack[] lokiSet = ItemUtils.generateHermesFullKit();
 player.getInventory().setHelmet(lokiSet[1]);
 player.getInventory().setChestplate(lokiSet[2]);
 player.getInventory().setLeggings(lokiSet[3]);
 player.getInventory().setBoots(lokiSet[4]);
 player.getInventory().setItem(0, lokiSet[0]);
 player.getInventory().setItem(1, new ItemStack(Material.ENDER_PEARL, 16));
 player.getInventory().setItem(2, new ItemStack(Material.POTION, 3, (short) 16398));
 player.getInventory().setItem(8, new ItemStack(Material.GOLDEN_APPLE, 3));
 player.addPotionEffect(new PotionEffect(PotionEffectType.SPEED, 20 * 300, 2));
 player.addPotionEffect(new PotionEffect(PotionEffectType.JUMP, 20 * 300, 1));
 player.addPotionEffect(new PotionEffect(PotionEffectType.NIGHT_VISION, 20 * 300, 0));
 for (int i = 0; i < 8; i++) {
 player.getWorld().playEffect(player.getLocation().add(0, i/2.0, 0), org.bukkit.Effect.SMOKE, 4);
 }
 player.playSound(player.getLocation(), Sound.ENDERMAN_TELEPORT, 1.0f, 0.5f);
 player.sendMessage(MessageUtils.getColoredMessage("&2&l[&a&lLoki&2&l] &a❈ &2¡Las sombras te abrazan, maestro del engaño!"));
 player.sendMessage(MessageUtils.getColoredMessage("&a&l➤ &7Has recibido el set completo de Loki"));
 break;
 case "freyja":
 ItemStack[] freyjaSet = ItemUtils.generateApolloFullKit();
 player.getInventory().setHelmet(freyjaSet[1]);
 player.getInventory().setChestplate(freyjaSet[2]);
 player.getInventory().setLeggings(freyjaSet[3]);
 player.getInventory().setBoots(freyjaSet[4]);
 player.getInventory().setItem(0, freyjaSet[0]);
 player.getInventory().setItem(1, new ItemStack(Material.DIAMOND_SWORD));
 player.getInventory().setItem(7, new ItemStack(Material.GOLDEN_APPLE, 5));
 player.getInventory().setItem(8, new ItemStack(Material.ARROW, 64));
