 for (int i = 0; i < 3; i++) {
 player.getWorld().playEffect(player.getLocation(), org.bukkit.Effect.HEART, 1);
 player.getWorld().playEffect(player.getLocation().add(Math.random()*2-1, 1, Math.random()*2-1), org.bukkit.Effect.HEART, 1);
 }
