 lokiLore.add(MessageUtils.getColoredMessage("&c⚠ Debilidad por balance"));
 lokiLore.add(MessageUtils.getColoredMessage("&8Click para elegir"));
 lokiMeta.setLore(lokiLore);
 loki.setItemMeta(lokiMeta);
